import React from "react";
import { Link } from 'react-router-dom';
import axios from "axios";
import { useState } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Form(props) {

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')

  const handleEmail = (e) => {
      setEmail(e.target.value);
  };

  const handlePassword = (e) => {
      setPassword(e.target.value);
  };

  const loginUser = async(e) =>{
    e.preventDefault()
    try {
      const response = await axios.post('http://localhost:4000/login', {email, password})

      if (response.status === 200) {

        const { accessToken, refreshToken } = response.data;

        //Save Access_Token in session storage
        sessionStorage.setItem('access_token', accessToken);
        sessionStorage.setItem('refresh_token', refreshToken);
      
        //Redirect or perform any other action upon successful login
        window.location.href = '/Home';
      } else {
        const errorResponse = await response.data
        setError(errorResponse);
      }
    } catch (error) {
      setError('An error occurred. Login the user Please try again.');
    }
  }

    return(
      <>
       <div className="OuterForm">
        <form onSubmit={props.submit}>
          <div className="form-group">
            <input type="email" required name="email" onChange={props.onChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
          </div>
          <div className="form-group">
            <input type="password" required name="password" onChange={props.onChangeTwo} className="form-control" id="exampleInputPassword1" placeholder="Password"/>
          </div>
          {props.error && <p className="text-danger">{props.error}</p>}
          <div className="Inline">
            <button type="submit">{props.btn}</button><p id="btnext">{props.question}<Link to={props.to} activeClassName="active">{props.btn2}</Link></p>
          </div>
        </form>
       </div> 
      </>
    )
}

export default Form;
