import {React, useState} from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { UserProvider } from './pages/UserContext';
import Home from './pages/Home';
import Cart from './pages/Cart';
import Shop from './pages/Shop';
import LogIn from './pages/LogIn';
import SignUp from './pages/SignUp';
import Profile from './pages/Profile';
import './App.css';

function App() {

    const [cartItems, setCartItems] = useState([]);
    const [purchasedItems, setPurchasedItems] = useState({});

    const handleBuy = (item) => {
      setCartItems([...cartItems, item]);
      setPurchasedItems({ ...purchasedItems, [item.title]: true });
    };

    const handleRemove = (itemTitle) => {
      setCartItems(cartItems.filter(item => item.title !== itemTitle));
      setPurchasedItems({ ...purchasedItems, [itemTitle]: false });
    };

  return (
      <>

      <UserProvider>
          <Routes>
              <Route path='/Cart' element={<Cart
              head="No Orders!"
              cartItems={cartItems} onRemove={handleRemove} 
              />}/>
              <Route path='/Home' element={<Home
              headpara="Introducing Story Shelfâ€”your ultimate online destination for storybooks! We offer a wide range of enchanting tales and captivating stories, delivered right to your doorstep. Dive into a world of imagination and adventure with Story Shelf, where every book opens a new chapter in your journey."
              head="Welcome to Sale Sphere." 
              headtwo="A better way to shop anywhere on Earth."
              headtwopara="This website offers you an elevated and more convenient shopping experience, allowing you to browse and make purchases with greater ease and satisfaction."
              btn="Shop"
              midscript="Up For Some Shopping?"
              moonscript="You have the power to place orders right from the moon's serene surface, adding a touch of celestial wonder to your shopping experience."
              />}/>
              <Route path='/Shop' element={<Shop onBuy={handleBuy} purchasedItems={purchasedItems}/>}/>
              <Route path='/SignUp' element={<SignUp/>}/>
              <Route path='/Profile' element={<Profile/>}/>
              <Route path='/' element={<LogIn/>}/>
          </Routes>
      </UserProvider>  

      </>
  );
}

export default App;
