import React, { useState } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';

function SignUp() {

   const [formData, setFormData] = useState({
      firstname: '',
      lastname: '',
      gender: '',
      date: '',
      email: '',
      password: '',
  });

  const handleChange = (e) => {
   const { name, value } = e.target;
   setFormData(prevState => ({
       ...prevState,
       [name]: value
   }));
};
   const handleSubmit = (e) => {
      e.preventDefault();
      axios.post('http://localhost:5001/signup', formData)
          .then(response => {
              console.log(response.data);
              // Redirect to login or show success message
          })
          .catch(error => {
              console.error('There was an error!', error);
          });
   };

    return(
        <>
         <div className="OuterForm">
          <form className="FormSign" onSubmit={handleSubmit}>
            <div class="Picon">
              <img src="./letter-s lighter1.png" height="50px" width="50px" alt="Logo" />
            </div>  
               <div className="Inline">
                  <div className="form-group">
                     <input type="text" name="firstname" className="form-control" id="firstname" placeholder="First Name" required onChange={handleChange} />
                  </div>
                  <div className="form-group">
                     <input type="text" name="lastname" className="form-control" id="lastname" placeholder="Last Name" required onChange={handleChange} />
                  </div>
               </div>
               <div className="form-group">
                  <select className="form-control" name="gender" required onChange={handleChange}>
                     <option value="" disabled selected>Select Your Gender</option>
                     <option value="Male">Male</option>
                     <option value="Female">Female</option>
                  </select>
               </div>
               <div class="date">
                   <input type="date" required name="date" className="dateGo" id="dateExample" onChange={handleChange}/>
               </div>
               <div class="EmandPas">
                    <input type="email" required name="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email" onChange={handleChange}/>
                    <input type="password" required name="password" className="form-control" id="exampleInputPassword1" placeholder="Password" onChange={handleChange}/>
               </div>
            <div className="Inline">
              <button type="submit">Sign Up</button><p id="btnext3">Already have an Account?<Link to="/" activeClassName="active">LogIn</Link></p>
            </div>
          </form>
         </div> 
        </>
      )
}

export default SignUp;
