import React from "react";
import Footer from "./Footer";
import Card from "./card";
import Navbar from "./Navbar";

function Shop({ onBuy, purchasedItems }) {

        const items = [
          { pic: "./mountbike.jpg", title: "Mountain Bike", para: "A fully functional Mountain Bike." },
          { pic: "./redbike.jpg", title: "A Red Bike", para: "An unused functional Normal Red Bicycle." },
          { pic: "./mouse.jpg", title: "A Mouse", para: "A fully functional unused Wireless Mouse." },
          { pic: "./adidas.jpg", title: "Sneakers", para: "Upgrade your footwear collection with our brand-new sneakers." },
          { pic: "./bed.jpg", title: "A Bed", para: "Get this comfortable bed." },
          { pic: "./controller.jpg", title: "A Controller", para: "A functional game controller." },
          { pic: "./twocontrollers.jpg", title: "Controllers", para: "Two brand new gaming controllers." },
          { pic: "./whitecontrollers.jpg", title: "White Colored Controllers", para: "A brand new white colored controllers." },
          { pic: "./mountbiketwo.jpg", title: "Mount Bike", para: "A fully functional Mountain Bike." },
          { pic: "./mountbikethree.jpg", title: "Bike", para: "A fully functional Mountain Bike." },
          { pic: "./comphotab.jpg", title: "PC, Tablet and CellPhone", para: "Buy PC, Tablet and CellPhone for an unbelievable price." },
          { pic: "./whitecontroller.jpg", title: "One White Controller", para: "One functional white game controller." }
        ];

    return(
        <>
        <Navbar/>
        <div className="Body">
      {items.map((item) => (
        <Card key={item.title} pic={item.pic} title={item.title} para={item.para}>
          <button onClick={() => onBuy(item)} disabled={!!purchasedItems[item.title]} style={{ backgroundColor: purchasedItems[item.title] ? 'grey' : 'rgba(13, 110, 264, 1)', color: '#fff', borderRadius: '5px', padding: '-15px', fontSize: '18px'}}>
            {purchasedItems[item.title] ? 'Sold' : 'Buy'}
          </button>
        </Card>
      ))}
    </div>
        <Footer/>
        </>
    )
}

export default Shop;
