import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from './UserContext';
import Footer from "./Footer";

function Profile() {
  const { user, setUser } = useContext(UserContext);
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    gender: '',
    date: '',
    address: '',
    profilePicture: null,
  });

  const [preview, setPreview] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
      setFormData({
        firstname: user.firstname || '',
        lastname: user.lastname || '',
        gender: user.gender || '',
        date: user.date ? user.date.split('T')[0] : '',
        address: user.address || '',
        profilePicture: user.profilePicture || null,
      });
      setPreview(user.profilePicture ? `data:image/png;base64,${user.profilePicture}` : null);
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handlePictureUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        profilePicture: file,
      });
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSubmit = new FormData();
      for (const key in formData) {
        if (key === 'profilePicture' && formData[key]) {
          formDataToSubmit.append(key, formData[key], formData[key].name);
        } else {
          formDataToSubmit.append(key, formData[key]);
        }
      }

      const response = await fetch('http://localhost:5008/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: formDataToSubmit,
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Profile updated successfully', data);
        setErrorMessage('');
        setUser(data);  // Update user context with new data
        navigate('/home');
      } else {
        const errorData = await response.json();
        console.error('Failed to update profile', errorData);
        setErrorMessage('Failed to update profile.');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('An error occurred. Please try again.');
    }
  };

  return (
    <>
      <div className="Profile">
        <form className="FormProfile" onSubmit={handleSubmit}>
          <div className="profile-picture-upload">
            <div className="profile-picture-preview">
              {preview ? (
                <img src={preview} alt="Profile Preview" />
              ) : (
                <div className="placeholder">No Image</div>
              )}
            </div>
            <div className="upload-container">
              <input type="file" id="file-input" onChange={handlePictureUpload} />
              <label htmlFor="file-input" className="file-input-label">
                Upload Photo
              </label>
            </div>
          </div>
          <div className="form-group">
            <label htmlFor="firstname">First Name:</label>
            <input
              type="text"
              id="firstname"
              name="firstname"
              value={formData.firstname}
              onChange={handleChange} required/>
          </div>
          <div className="form-group">
            <label htmlFor="lastname">Last Name:</label>
            <input type="text" id="lastname" name="lastname" value={formData.lastname} onChange={handleChange} required/>
          </div>
          <div className="form-group">
            <label htmlFor="gender">Gender:</label>
            <select id="gender" name="gender" value={formData.gender} onChange={handleChange} required>
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="date">Date of Birth:</label>
            <input type="date" id="date" name="date" value={formData.date} onChange={handleChange} required/>
          </div>
          <div className="form-group">
            <label htmlFor="address">Address:</label>
            <input type="text" id="address" name="address" value={formData.address} onChange={handleChange} required/>
          </div>
          {errorMessage && <p className="error-message">{errorMessage}</p>}
          <button type="submit">Update Profile</button>
        </form>
      </div>
      <Footer />
    </>
  );
}

export default Profile;
