import React from "react";
import Form from "./Form";
import axios from "axios";
import { useState } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function LogIn(props) {

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')

  const handleEmail = (e) => {
      setEmail(e.target.value);
  };

  const handlePassword = (e) => {
      setPassword(e.target.value);
  };

  const loginUser = async(e) =>{
    e.preventDefault()
    try {
      const response = await axios.post('http://localhost:4000/login', {email, password})

      if (response.status === 200) {

        const { accessToken, refreshToken } = response.data;

        //Save Access_Token in session storage
        sessionStorage.setItem('access_token', accessToken);
        sessionStorage.setItem('refresh_token', refreshToken);
      
        //Redirect or perform any other action upon successful login
        window.location.href = '/Home';
      } else {
        const errorResponse = await response.data
        setError(errorResponse);
      }
    } catch (error) {
      setError('An error occurred. Please try again.');
    }
  }



    return(
      <Form error={error} submit={loginUser} onChange={handleEmail} onChangeTwo={handlePassword} btn="Log In" btn2="Sign Up" question="Dont have an Account?" to="/Register"/>
    )
}

export default LogIn;
