import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

function Profile() {
  const [formData, setFormData] = useState({
    firstname: '',
    lastname: '',
    gender: '',
    date: '',
    address: '',
    email: '',  // Assuming email should be editable
    profilePicture: null,
  });

  const [preview, setPreview] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the user details from the server
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('http://localhost:5008/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,  // Assuming JWT token is stored in localStorage
          },
        });

        if (response.ok) {
          const data = await response.json();
          setFormData({
            ...data,
            profilePicture: data.profilePicture || null,
          });
          setPreview(data.profilePicture ? `data:image/png;base64,${data.profilePicture}` : null);
        } else {
          const errorData = await response.json();
          console.error('Failed to fetch user details', errorData);
        }
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchUserDetails();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handlePictureUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        profilePicture: file,
      });
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSubmit = new FormData();
      for (const key in formData) {
        formDataToSubmit.append(key, formData[key]);
      }

      const response = await fetch('http://localhost:5008/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: formDataToSubmit,
      });

      if (response.ok) {
        console.log('Profile updated successfully');
        setErrorMessage('');
        navigate('/Home');
      } else {
        const errorData = await response.json();
        console.error('Failed to update profile', errorData);
        setErrorMessage('Failed to update profile.');
      }
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('An error occurred. Please try again.');
    }
  };

  return (
    <>
      <div className="Profile">
        <form className="FormProfile" onSubmit={handleSubmit}>
          <div className="profile-picture-upload">
            <div className="profile-picture-preview">
              {preview ? (
                <img src={preview} alt="Profile Preview" />
              ) : (
                <div className="placeholder">No Image</div>
              )}
            </div>
            <div className="upload-container">
              <input type="file" id="file-input" onChange={handlePictureUpload} />
              <label htmlFor="file-input">
                {formData.profilePicture ? "Change Photo" : "Add Photo"}
              </label>
            </div>
          </div>
          <div className="Inline">
                    <div className="form-group">
                        <input type="text" name="firstname" className="form-control" id="firstname" placeholder="First Name" required value={formData.firstname} onChange={handleChange}/>
                    </div>
              <div className="form-group">
                <input type="text" name="lastname" className="form-control" id="lastname" placeholder="Last Name" required value={formData.lastname} onChange={handleChange}/>
              </div>
          </div>
          <div className="form-group">
            <select className="form-control" name="gender" value={formData.gender} onChange={handleChange}>
              <option value="" disabled>Select Your Gender</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
            </select>
          </div>
          <div className="form-group">
            <input type="date" name="date" className="form-control" value={formData.date} onChange={handleChange} />
            <input type="text" name="address" className="form-control" placeholder="Address" value={formData.address} onChange={handleChange} />
          </div>
          <div className="form-group">
            <input type="email" name="email" className="form-control" placeholder="Email" value={formData.email} onChange={handleChange} />
          </div>
          {errorMessage && <p>{errorMessage}</p>}
          <button type="submit">Save</button>
        </form>
      </div>
      <Footer />
    </>
  );
}

export default Profile;
