import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';

function LogIn() {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });

    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();  // useNavigate hook

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5003/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok) {
                console.log('User logged in successfully');
                setErrorMessage('');
                navigate('/Home');  // Navigate to home page
            } else {
                const errorData = await response.json();
                console.error('Failed to log in', errorData);
                setErrorMessage('Failed to log in');
            }
        } catch (error) {
            console.error('Error:', error);
            setErrorMessage('An error occurred. Please try again.');
        }
    };

    return (
        <>
            <div className="OuterForm2">
                <form className="FormLog" onSubmit={handleSubmit}>
                    <div className="Picon">
                        <h2>Welcome to SaleSphere</h2>
                    </div>    
                    <div className="form-group">
                        <input 
                            type="email" 
                            required 
                            name="email" 
                            className="form-control" 
                            id="exampleInputEmail1" 
                            aria-describedby="emailHelp" 
                            placeholder="Enter email" 
                            value={formData.email} 
                            onChange={handleChange} 
                        />
                    </div>
                    <div className="form-group">
                        <input 
                            type="password" 
                            required 
                            name="password" className="form-control" id="exampleInputPassword1" placeholder="Password" value={formData.password}  onChange={handleChange}/>
                    </div>
                    <p>{errorMessage}</p>
                    <div className="Inline">
                        <button type="submit">Log In</button>
                        <p id="btnext3">Don't have an account? <Link to="/SignUp" activeClassName="active">Sign Up</Link></p>
                    </div>
                </form>
            </div> 
        </>
    );
}

export default LogIn;
